import styled from "styled-components";

export const FooterContainer = styled.div`
  height: 15em;
  position: static;
  display: grid;
  width: 100%;
  min-height: auto;
  grid-template-columns: 100%;
  background-color: #303132;
  @media screen and (max-width: 800px) {
    padding: 10px;
    height: 60px;
    margin-bottom: 20px;
  }
`;

export const SocialNetworkContainer = styled.div`
  position: relative;
  margin: 29px 0px 20px calc((100% - 980px) * 0.5);
  left: 49px;
  grid-area: 1 / 1 / 2 / 2;
  justify-self: start;
  align-self: start;
  color: #d3d3d3;
`;

export const SocialNetworLogo = styled.div`
  position: relative;
  margin: 29px 0px 20px calc((100% - 980px) * 0.5);
  left: 49px;
  grid-area: 1 / 1 / 2 / 2;
  justify-self: start;
  align-self: start;
  color: #d3d3d3;
`;

export const NeedAssistanceContainer = styled.div``;
export const NeedAssistanceTitle = styled.span`
  position: relative;
  margin: 28px 0px 10px calc((100% - 600px) * 0.5);
  left: 740px;
  grid-area: 1 / 1 / 4 / 2;
  justify-self: start;
  align-self: start;
  color: #d3d3d3;
`;

export const PhoneNumber = styled.span`
  text-align: center;
  line-height: 1.6em;
  color: white;
  letter-spacing: 0.1em;
`;

export const CopyrightContainer = styled.div`
  position: relative;
  margin: 0px 0px 39px calc((100% - 480px) * 0.5);
  left: 200px;
  grid-area: 4 / 1 / 5 / 2;
