import React from "react";
import { ReactComponent as Logo } from "../../assests/crown.svg";
import { connect } from "react-redux";
import CartIcon from "./../cart-icon/cart-icon";
import CartDropdown from "./../cart-dropdown/cart-dropdown";
import { selectCurrentUser } from "./../../redux/user/user.selector";
import { selectCartHidden } from "../../redux/cart/cart.selector";
import { createStructuredSelector } from "reselect";
import {
  HeaderContainer,
  LogoContainer,
  OptionsContainer,
  OptionLink,
} from "./header.styles";
import { signOutStart } from "../../redux/user/user.action";
import { TransitionGroup, CSSTransition } from "react-transition-group";
import styled from "styled-components";

const Header = ({ currentUser, hidden, signOutStart }) => (
  <HeaderContainer>
    <LogoContainer to="/">
      <Logo className="logo" />
    </LogoContainer>
    <OptionsContainer>
      <OptionLink to="/shop">SHOP</OptionLink>
      <OptionLink to="/shop">CONTACT</OptionLink>
      {currentUser ? (
        <OptionLink as="div" onClick={signOutStart}>
          SIGN OUT
        </OptionLink>
      ) : (
        <OptionLink to="/signin">SIGN IN</OptionLink>
      )}
      <CartIcon />
    </OptionsContainer>
    {
      <Wrapper>
        <CSSTransition
          key={1}
          in={hidden}
          appear
          timeout={{ enter: 300, exit: 300 }}
          classNames="display"
        >
          <section className="display-enter">
            <CartDropdown />
          </section>
        </CSSTransition>
      </Wrapper>
    }
  </HeaderContainer>
);

const mapStateToProps = createStructuredSelector({
  currentUser: selectCurrentUser,
  hidden: selectCartHidden,
});

const mapDispatchToProps = (dispatch) => ({
  signOutStart: () => dispatch(signOutStart()),
});

const Wrapper = styled.div`
  .display-enter,
  .display-appear {
    top: var(--menu-starting-top);
    width: var(--toggler-width);
    max-height: var(--toggler-height);
    color: var(--fade-from-color);
    background-color: var(--toggler-bg-color);
  }
  .display-enter-active,
  .display-appear-active {
    top: var(--menu-ending-top);
    width: var(--menu-width);
    max-height: var(--menu-max-height);
    color: var(--fade-to-color);
    background-color: var(--menu-bg-color);
    transition: all var(--timeout);
  }
`;
export default connect(mapStateToProps, mapDispatchToProps)(Header);
