import React from "react";
import {
  CartItemContainer,
  ItemDetailsContainer,
  CartItemImage,
} from "./cart-item.styles";
import {
  clearItemFromCart,
  /*  addItem,
  removeItem */
} from "./../../redux/cart/cart.action";
import { RemoveButtonContainer } from "./../checkout-item/checkout-item.styles";

const CartItem = ({ item: { imageUrl, price, name, quantity }, clearItem }) => (
  <CartItemContainer>
    <CartItemImage src={imageUrl} alt="item" />
    <ItemDetailsContainer>
      <span>{name}</span>
      <span>
        {quantity} x ${price}
      </span>
      <RemoveButtonContainer onClick={() => clearItem(item)}>
        &#10005;
        {/* &#10005 is utf-8 Dingbats */}
      </RemoveButtonContainer>
    </ItemDetailsContainer>
  </CartItemContainer>
);

const mapDispatchToProps = (dispatch) => ({
  clearItem: (item) => dispatch(clearItemFromCart(item)),
  /*   addItem: item => dispatch(addItem(item)),
  removeItem: item => dispatch(removeItem(item)) */
});

export default connect(null, mapDispatchToProps)(React.memo(CartItem));
