import styled from "styled-components";

export const FooterContainer = styled.div`
  height: 180px;
  width: 100%;
  display: flex;
  justify-content: space-between;
  padding: 0px 60px;
  background-color: #535151;
  @media screen and (max-width: 800px) {
    padding: 10px;
    height: 60px;
    margin-bottom: 20px;
  }
`;
